import { useState } from "react";
import Infomation from "../molecules/Infomation";

export const Form = () => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");

  const handleSubmit = (event: React.FormEvent) => {
    event.preventDefault();
    // フォームの送信処理を追加
    console.log("Name:", name, "Phone:", phone);
  };

  return (
    <>
      <Infomation
        talk="最後の質問です、名前、電話番号を教えてください"
        officeName="司法書士法人みどり法務事務所"
      />
      <div className="flex flex-col items-center bg-white rounded-lg p-4 mx-4 mt-4 shadow-lg">
        <form onSubmit={handleSubmit} className="w-full max-w-sm mt-4">
          <div className="mb-4">
            <label
              className="block text-gray-700 text-sm font-bold mb-2"
              htmlFor="name"
            >
              名前
            </label>
            <input
              id="name"
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="名前を入力してください"
              required
            />
          </div>
          <div className="mb-4">
            <label
              className="block text-gray-700 text-sm font-bold mb-2"
              htmlFor="phone"
            >
              電話番号
            </label>
            <input
              id="phone"
              type="tel"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              placeholder="電話番号を入力してください"
              required
            />
          </div>
          <div className="flex items-center justify-between">
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            >
              送信
            </button>
          </div>
        </form>
      </div>
    </>
  );
};
