import React from "react";

interface InfomationProps {
  talk: string;
}

const Infomation: React.FC<InfomationProps> = ({ talk }) => {
  return (
    <>
      <div className="flex items-start mt-4 ml-40">
        <img
          src="./image/lawyer.png"
          alt="アイコン"
          className="w-10 h-10 rounded-full mr-4"
        />
      </div>
      <div className="bg-white text-black px-4 py-2 rounded-lg shadow-lg max-w-2xl mt-4 relative ml-40 ">
        <div
          className="absolute bottom-6 left-0 transform translate-x-[-50%] translate-y-[50%] w-0 h-0 
       border-t-transparent border-r-transparent border-b-[20px] border-b-white border-l-[20px] border-l-transparent"
        ></div>
        <p>{talk}</p>
      </div>
    </>
  );
};

export default Infomation;
