import React from "react";

interface InfomationProps {
  talk: string;
}

const Infomation: React.FC<InfomationProps> = ({ talk }) => {
  return (
    <div className="relative bg-blue-400 text-white px-4 py-2 rounded-t-lg rounded-br-lg shadow-lg max-w-xs mt-4">
      <p>{talk}</p>
      <div className="absolute left-0 bottom-0 transform translate-x-6 translate-y-6 w-0 h-0 border-t-[20px] border-t-transparent border-b-[20px] border-b-blue-400 border-r-[20px] border-r-transparent"></div>
    </div>
  );
};

export default Infomation;
