import React from "react";

interface InfomationProps {
  talk: string;
}

const Infomation: React.FC<InfomationProps> = ({ talk }) => {
  return (
    <div className="flex justify-end">
      <div className="bg-white text-black px-4 py-2 rounded-lg shadow-lg max-w-2xl mt-4 relative">
        <p>{talk}</p>
      </div>
    </div>
  );
};

export default Infomation;
