import React from "react";
import lawyer from "../atoms/image/lawyer.png";

interface InfomationProps {
  talk: string;
  officeName: string;
}

const Infomation: React.FC<InfomationProps> = ({ talk, officeName }) => {
  return (
    <div className="flex items-start mt-4 ml-5">
      <img
        src={lawyer}
        alt="アイコン"
        className="w-20 h-20 rounded-full mr-4"
      />

      <div className="bg-white text-black px-4 py-2 rounded-lg shadow-lg max-w-2xl mt-4 relative ml-0 ">
        <p className="font-bold absolute -top-6 ">{officeName}</p>
        <div
          className="absolute bottom-6 left-0 transform translate-x-[-50%] translate-y-[50%] w-0 h-0 
          border-t-transparent border-r-transparent border-b-[20px] border-b-white border-l-[20px] border-l-transparent pt-20"
        ></div>
        <p>{talk}</p>
      </div>
    </div>
  );
};

export default Infomation;
