import React from "react";

interface InfomationProps {
  talk: string;
}

const Infomation: React.FC<InfomationProps> = ({ talk }) => {
  return (
    <div className="bg-white text-black px-4 py-2 rounded-lg shadow-lg max-w-2xl mt-4 relative ml-12 ">
      <div
        className="absolute left-0 bottom-7 transform translate-x-8
       translate-y-8 w-0 h-0 border-t-[20px] border-t-transparent border-b-[20px] 
       border-b-white border-r-[20px] border-r-transparent"
      ></div>
      <p>{talk}</p>
    </div>
  );
};

export default Infomation;
